import React from "react";

// import { BrowserRouter as Router, Route, Link } from "react-router-dom";

// material ui
import Paper from '@material-ui/core/Paper';
import Container from '@material-ui/core/Container';
import { Button } from "@material-ui/core";
import Typography from '@material-ui/core/Typography';

// import { fs } from "../../../config/firebase";

class WorkshopComponent extends React.Component {


    // constructor
    constructor(props) {

        // constructur of parent
        super(props);

        this.setState = {
            loading: true,
        };

    }

    render() {

        // I don't know why this way works in the server
        const workshops = [];

        this.props.workshops.forEach((course, idx) =>

            // add each course to the list of elements
            workshops.push(
                // each course
                <Paper
                    key={idx}
                    style={{
                        margin: 20,
                        padding: 20,
                    }}
                    elevation={20}
                >

                    {/* course name */}
                    <Typography align="center" variant="body2" component="p" gutterBottom>
                        {course.name}
                    </Typography>

                    {/* edit course
                    <Button align="center" variant="contained" color="primary"
                        size="small"
                        onClick={() => {

                            console.log(this.props);

                            this.props.history.push('/createNewCourse', { course: course, editCourse: true });
                        }}
                    >
                        Edit
                    </Button> */}

                </Paper>

            )

        )

        // return method
        return (

            <Container>

                {/* each workshop */}
                {
                    workshops

                }

            </Container>

        );

    }

}

export default WorkshopComponent;
