import React from "react";

// import { BrowserRouter as Router, Route, Link } from "react-router-dom";

// material ui
import Paper from '@material-ui/core/Paper';
import Container from '@material-ui/core/Container';
import { Button } from "@material-ui/core";
import Typography from '@material-ui/core/Typography';

// import { fs } from "../../../config/firebase";

class WorkshopComponent extends React.Component {


    // constructor
    constructor(props) {

        // constructur of parent
        super(props);

        this.setState = {
            loading: true,
        };

    }

    render() {

        // I don't know why this way works in the server
        const courses = [];

        this.props.courses.forEach((course, idx) =>

            // add each course to the list of elements
            courses.push(
                // each course
                <Paper
                    key={idx}
                    style={{
                        margin: 20,
                        padding: 20,
                    }}
                    elevation={20}
                >

                    {/* course name */}
                    <Typography align="center" variant="body2" component="p" gutterBottom>
                        {course.course_name}
                    </Typography>

                    {/* course registered users */}
                    <Typography align="center" variant="body2" component="p" gutterBottom>
                        Revenues: {course.course_revenues != null ? course.cours_revenues : 0} USD
                    </Typography>

                    {/* course registered users */}
                    <Typography align="center" variant="body2" component="p" gutterBottom>
                        Registered users: {course.registered_users.length}
                    </Typography>


                    {/* course state */}
                    <Typography align="center" variant="body2" component="p" gutterBottom>
                        State: {course.published ? "Published" : "Not published yet"}
                    </Typography>

                    {/* edit course */}
                    <Button align="center" variant="contained" color="primary"
                        size="small"
                        onClick={() => {

                            console.log(this.props);

                            this.props.history.push('/createNewCourse', { course: course, editCourse: true });
                        }}
                    >
                        Edit
                    </Button>

                    {/* publish / unpublish course */}
                    <Button align="center" variant="contained"
                        color={course.published ? "secondary" : "primary"}
                        size="small"
                        onClick={() => {

                            // console.log(this.props);
                            // this.deleteCourse(course);
                            this.publishCourse(course);

                        }}
                    >

                        {course.published ? "Unpublish" : "Publish"}

                    </Button>

                    {/* delete course */}
                    <Button align="center" variant="contained" color="secondary"
                        size="small"
                        onClick={() => {

                            // console.log(this.props);
                            this.deleteCourse(course);

                        }}
                    >
                        Delete
                    </Button>

                </Paper>

            )

        )

        // return method
        return (

            <Container>

                {/* each course */}
                {
                    courses
                    // // courses
                    // this.props.courses.map( (course, idx) =>

                    //     {
                    //         return (


                    //             // each course
                    //             <Paper
                    //                 key = {idx}
                    //                 style={{
                    //                     margin: 20,
                    //                     padding: 20,
                    //                 }}
                    //                 elevation = {20}
                    //             >

                    //                 {/* course name */}
                    //                 <Typography align="center" variant="body2" component="p" gutterBottom>
                    //                     {course.course_name}
                    //                 </Typography>

                    //                 {/* course name */}
                    //                 <Typography align="center" variant="body2" component="p" gutterBottom>
                    //                     State: {course.published ? "Published" : "Not published yet"}
                    //                 </Typography>

                    //                 {/* edit course */}
                    //                 <Button align="center" variant="contained" color="primary" 
                    //                     size="small"
                    //                     onClick = {() => {
                    //                         this.props.history.push('/editCourse', {course: course});
                    //                     }}
                    //                     >
                    //                     Edit
                    //                 </Button>

                    //             </Paper>

                    //         )
                    //     }             

                    // )

                }

            </Container>

        );

    }

}

export default WorkshopComponent;
