import React from "react";

// import { BrowserRouter as Router, Route, Link } from "react-router-dom";

// material ui
import Paper from '@material-ui/core/Paper';
import Container from '@material-ui/core/Container';
import { Button } from "@material-ui/core";
import Typography from '@material-ui/core/Typography';
import CircularProgress from "@material-ui/core/CircularProgress";

//  component
import WorkshopComponent from "./components/workshop.component";

// firebase
// import { auth } from "../../config/firebase";
// import { fs } from "../../config/firebase";

// workshop mockup
const _workshops = [
    {
        "name": "Yoga indio",
        "teacher": "Daniela Parra",
        "description": "Unete a nuestras clases de yoga, para hacer ejercicios y sacarte el estrés que tienes dentro",
    },

    {
        "name": "Baile entretenido",
        "teacher": "Rodrigo Días",
        "description": "Unete a nuestras clases unicas de baile entretenido",
    },
];
class Home extends React.Component {

    // constructor
    constructor(props) {

        // constructur of parent
        super(props);

        // initial states
        this.state = {
            loading: true,
            workshops: [],
            // email: "",
            // password: "",
        }

    }

    componentDidMount() {

        this.setState({
            workshops: _workshops,
            loading: false,
        });

    }

    render() {

         // I don't know why this way works in the server
        const workshops = [];

        this.state.workshops.forEach( (course, idx) => 

            // add each course to the list of elements
            workshops.push(
                // each course
                <Paper
                    key = {idx}
                    style={{
                        margin: 20,
                        padding: 20,
                    }}
                    elevation = {20}
                >

                    {/* course name */}
                    <Typography align="center" variant="body2" component="p" gutterBottom>
                        {course.name}
                    </Typography>


                    {/* edit course
                    <Button align="center" variant="contained" color="primary" 
                        size="small"
                        onClick = {() => {
                            this.props.history.push('/editCourse', {course: course});
                        }}
                    >
                        Edit
                    </Button> */}

                </Paper>

            )

        )

        // return method
        return (

            <Paper
                style={{
                    margin: 20,
                    padding: 20,
                }}
            >

                {/* workshops */}
                <Container>

                    {
                        !this.state.loading

                            ?

                            <Container>

                                <workshopsComponent
                                    workshops={this.state.workshops}
                                    history={this.props.history}
                                />

                                }

                            </Container>

                            :

                            <CircularProgress />

                    }

                </Container>

            </Paper>
        );

    }

}

export default Home;
